using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class LevelLoader:MonoBehaviour{public Texture2D cursorArrow;public Animator transition;public float transitionTime=1f;void Start(){Cursor.SetCursor(cursorArrow, Vector2.zero, CursorMode.ForceSoftware);}void Update(){if(Input.GetMouseButtonDown(0) | Input.anyKey) {LoadNextLevel();}}public void LoadNextLevel(){StartCoroutine(LoadLevel(SceneManager.GetActiveScene().buildIndex + 1));}IEnumerator LoadLevel(int levelIndex){transition.SetTrigger("Start");yield return new WaitForSeconds(transitionTime);SceneManager.LoadScene(levelIndex);}}