using UnityEngine;using System.Collections;using UnityEngine.SceneManagement;public class SwitchScene:MonoBehaviour{public GameObject warning,newgame,back,option,credits,quit,load;public void NextScene(string sceneName){if(PlayerPrefs.HasKey("hp")){warning.SetActive(true);newgame.SetActive(false);back.SetActive(false);option.SetActive(false);credits.SetActive(false);quit.SetActive(false);load.SetActive(false);}else{SceneManager.LoadScene("sc1");}}}