using UnityEngine;using UnityEngine.SceneManagement;public class confirmOrnot:MonoBehaviour{public GameObject newgame,back,load,quit,credits,option,warning;void Update(){if(Input.GetButtonDown("Fire1")){PlayerPrefs.DeleteAll();SceneManager.LoadScene("sc1");newgame.SetActive(false);back.SetActive(false);option.SetActive(false);credits.SetActive(false);quit.SetActive(false);load.SetActive(false);}else if (Input.GetButtonDown("Fire2")){newgame.SetActive(true);back.SetActive(true);option.SetActive(true);credits.SetActive(true);quit.SetActive(true);load.SetActive(true);warning.SetActive(false);}}}