using UnityEngine;using UnityEngine.UI;public class newHPbar:MonoBehaviour{public wAXE_health playerHealth;public Image fillImage;private Slider slider;void Start(){slider=GetComponent<Slider>();}void Update(){if(slider.value<=slider.minValue){fillImage.enabled=false;}if(slider.value>slider.minValue&&!fillImage.enabled){fillImage.enabled=true;}float fillValue=playerHealth.currentHealth/playerHealth.maxHealth;if(fillValue<=slider.maxValue/4.5){fillImage.color=Color.red;}else if(fillValue<=slider.maxValue/2){fillImage.color=Color.yellow;}else if(fillValue>slider.maxValue/3){fillImage.color=new Color32(106,178,26,255);}slider.value=fillValue;}}