using System.Collections;using System.Collections.Generic;using UnityEngine;public class messagedetect1:MonoBehaviour{public Animator anim;public GameObject mainmissiontext1,message1;public Animator mainmissiontext1anim;void Start(){anim=message1.GetComponent<Animator>();mainmissiontext1anim=mainmissiontext1.GetComponent<Animator>();}void OnTriggerEnter(Collider other){if(other.gameObject.tag=="Player"){anim.Play("message1");message1.SetActive(true);mainmissiontext1.SetActive(true);Destroy(this.gameObject, 11);}}}